mapper.update:
update orders
number = #{number}, status = #{status}, user_id = #{userId}, address_book_id = #{addressBookId}, order_time = #{orderTime}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, amount = #{amount}, remark = #{remark}, phone = #{phone}, address = #{address}, user_name = #{userName}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, pack_amount = #{packAmount}, tableware_number = #{tablewareNumber}, tableware_status = #{tablewareStatus},
where id = #{id};

mapper.select:
select id, number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount, remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time, estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status from orders
and number like concat('%',#{number},'%') and phone like concat('%',#{phone},'%') and status = #{status} and order_time between #{beginTime} and #{endTime}
order by order_time desc

select tab.total_number, tab.complete_number, ROUND(tab.complete_number / tab.total_number, 4) round
from (select count(*)                                    total_number,
             count(if(pay_status = 1, pay_status, null)) valid_number,
             count(if(status = 5, status, null))         complete_number
      from orders
      where order_time between #{begin} and #{end}) tab;

select temp.day,count(orders.id) total , count(if(status=5,status,null)) valid_number
from (

    select #{item}

        day


) as temp
left join orders on DATE(orders.order_time)=temp.day
group by temp.day
